import { Component} from '@angular/core';
import { NgForm } from "@angular/forms"

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = '04_Forms_Handling';
  genders:string[] = ['male','female'];
  answer:string="";

  onSubmit(f:NgForm){
    console.log("Submitted",f);
  }

  usernameSuggestion(form:NgForm){
    // Set all the values at once
    // form.setValue({
    //   userData:{
    //     username:"suggestedUsername",
    //     email:""
    //   },
    //   gender:"",
    //   secret:"pet",
    //   questionAnswer:""
    // })
  }
}
<div class="container">
  <div class="row">
    <div class="col-xs-12 col-sm-10 col-md-8 col-sm-offset-1 col-md-offset-2">
      <form (ngSubmit)="onSubmit(form)" #form="ngForm">
        <div id="user-data" ngModelGroup="userData">
          <div class="form-group">
            <label for="username">Username</label>
            <input required ngModel #username="ngModel" name="username" type="text" id="username" class="form-control">
            <span *ngIf="!username.valid && username.touched"  class="help-block">Username is not valid</span>
          </div>
          <button (click)="usernameSuggestion(form)" class="btn btn-default" type="button">Suggest an Username</button>
          <div class="form-group">
            <label for="email">Mail</label>
            <input  ngModel name="email" type="email" id="email" class="form-control" email>
          </div>
        </div>
        <div class="form-group">
          <label for="secret">Secret Questions</label>
          <select id="secret" name="secret" [ngModel]="'pet'" class="form-control">
            <option value="pet">Your first Pet?</option>
            <option value="teacher">Your first teacher?</option>
          </select>
        </div>
        <div class="form-group">
          <textarea name="questionAnswer" id="" class="form-control" [(ngModel)]="answer"></textarea>
        </div>
        <p>Your Reply : {{answer}}</p>
        <div class="radio" *ngFor="let gender of genders">
            <label for="">
              <input type="radio" name="gender" [value]="gender" [ngModel]="'male'" id="">
              {{gender}}
            </label>
        </div>
        <button [disabled]="!form.valid" class="btn btn-primary" type="submit">Submit</button>
      </form>
    </div>
  </div>
</div>
