import { Component, OnInit } from '@angular/core';
import { Recipe } from '../recipe.model';
import { RecipeService } from '../recipe.service';

@Component({
  selector: 'app-recipe-detail',
  templateUrl: './recipe-detail.component.html',
  styleUrls: ['./recipe-detail.component.css']
})
export class RecipeDetailComponent implements OnInit{

  recipeToDisplay:Recipe = new Recipe();

  constructor(private recipeService:RecipeService){}

  ngOnInit(): void {
    this.recipeService.recipeSelectedEvent.subscribe(($event)=>{
      this.recipeToDisplay = $event;
    })
  }
}
<div class="row">
  <div class="col-md-12">
    <img
    [ngStyle]="{
      maxHeight:'200px'
    }"
    src={{recipeToDisplay.imagePath}}
    class="img-responsive" alt="">
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <h1>{{recipeToDisplay.name}}</h1>
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <div class="btn-group" appDropdown>
      <button type="button" class="btn btn-primary dropdown-toggle">Manage Recipe <span class="caret"></span></button>
      <ul class="dropdown-menu">
        <li><a href="#">Add Ingridents</a></li>
        <li><a href="#">Edit Recipe</a></li>
        <li><a href="#">Delete Recipe</a></li>
      </ul>
    </div>
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <p>{{recipeToDisplay.description}}</p>
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <ul class="list-group">
      <li *ngFor="let ingredient of recipeToDisplay.ingredients">
        {{ingredient.name}}
      </li>
    </ul>
  </div>
</div>
