import { Directive,HostListener } from '@angular/core';

@Directive({
  selector: '[appDropdown]'
})
export class DropdownDirective {

  @HostListener("click") onClick($event:Event){
    console.log($event);
    (<HTMLElement>$event.target).classList.add('open');
  }

  constructor() { }

}
