import { Component, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { AuthService } from './auth.service';
import { Subject } from 'rxjs';
import { User } from './user.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent {
  isLogin = false;
  isLoading = false;

  constructor(private authSrv:AuthService,private router:Router){}

  @ViewChild("authForm")
  authFrom!:NgForm;

  switchMode(){
    this.isLogin = !this.isLogin;
  }

  onAuth(authF:NgForm){
    this.isLoading = true;
    if(this.isLogin){
      this.authSrv.signin(authF.value).subscribe((data)=>{
        this.isLoading = false;
        this.router.navigate(["/recipes"]);
      })
    } else {
      this.authSrv.signUp(authF.value).subscribe((data)=>{
        this.isLoading = false;
        console.log(data);
      });
    }
  }
}
<div class="row">
  <div class="col-xs-12 col-md-6 col-md-offset-3">
    <form #authForm="ngForm" (ngSubmit)="onAuth(authForm)">
      <div class="form-group">
        <label for="email">Email</label>
        <input ngModel type="text" name="email" id="email" required email class="form-control">
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input ngModel type="text" name="password" id="password" class="form-control">
      </div>
      <div *ngIf="!isLoading">
        <button   type="submit" class="btn btn-primary">{{isLogin ?"Signin": "Signup"}} </button>
        &nbsp;
        <button (click)="switchMode()" type="button" class="btn btn-default">{{isLogin ?"Switch To Signup" :'Switch To Login'}}</button>
      </div>
      <div *ngIf="isLoading">
        <button class="btn btn-default md-col-12">
          <app-loader></app-loader>
        </button>
      </div>
    </form>
  </div>
</div>
