import { Directive,HostListener } from '@angular/core';

@Directive({
  selector: '[appDropdown]'
})
export class DropdownDirective {

  @HostListener("click") onClick(e:Event){
    console.log(e)
    (<HTMLElement>e.target).classList.add('open');
  }

  constructor() { }

}
