import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { DataStorageService } from '../shared/data-storage.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  constructor(private router:Router,private rStorageSrv:DataStorageService){}

  navigateProg(path:string){
    this.router.navigate([path]);
  }
  newRecipe(){
    this.router.navigate(["/recipe","new"]);
  }
  onSaveRecipe(){
    this.rStorageSrv.saveRecipes();
  }
}
<header class="navbar navbar-default">
  <div class="container">
    <div class="navbar-header">
      <button type="button" class="navbar-toggle collapsed" data-toggle="collapse" data-target="#navbar-collapse">
        <span class="sr-only">Toggle Navigation</span>
        <span class="icon-bar"></span>
        <span class="icon-bar"></span>
        <span class="icon-bar"></span>
      </button>
      <a class="navbar-brand" href="#">Angular</a>
    </div>

    <div class="collapse navbar-collapse" id="navbar-collapse">
      <ul class="nav navbar-nav">
        <li routerLinkActive="active" ><a routerLink="/recipe" >Recipes</a></li>
        <li routerLinkActive="active"><a routerLink="/shopping" >Shopping List</a></li>
        <!-- <li routerLinkActive="active"> <a (click)="navigateProg('/')" role="button" class="btn btn-default">Home</a></li>
        <li routerLinkActive="active"><a (click)="navigateProg('/shopping')" role="button" class="btn btn-default">Shopping</a></li> -->
        <style>
          .navbar-nav li a[role="button"]{
            margin-right: 10px;
            color: black;
          }
        </style>
      </ul>
      <ul class="nav navbar-nav navbar-right">
        <li class="" [ngStyle]="{display:'flex',alignItem:'center',marginTop:'10px'}">
            <button class="btn btn-success" (click)="newRecipe()">New Recipe</button>
        </li>
        <li class="dropdown" appDropdown>
          <a style="cursor: pointer;" class="dropdown-toggle" role="button" >Manage <span class="caret"></span></a>
          <ul class="dropdown-menu">
            <li><a style="cursor: pointer;" (click)="onSaveRecipe()">save data</a></li>
            <li><a style="cursor: pointer;">fetch data</a></li>
          </ul>
        </li>

      </ul>
    </div>
  </div>
</header>
