import { Component } from '@angular/core';
import { Ingredient } from '../shared/ingredient.model';

@Component({
  selector: 'app-shopping-lists',
  templateUrl: './shopping-lists.component.html',
  styleUrls: ['./shopping-lists.component.css']
})
export class ShoppingListsComponent {

  ingredients:Ingredient[] = [
    new Ingredient("Ingredient1",5),
    new Ingredient("Ingredint2",10)
  ];

  addNewIngredient(e:Ingredient){
    this.ingredients.push(e)
  }

}
<div class="row">
  <div class="col-md-12">
    <app-shopping-edit (onAddNewIngredientEvent)="addNewIngredient($event)"></app-shopping-edit>
    <hr>
    <ul class="list-group">
      <a class="list-group-item" style="cursor: pointer;" *ngFor="let ingredient of ingredients">
        {{ingredient.name}}
        {{ingredient.amount}}
      </a>
    </ul>
  </div>
</div>
