import { Component,OnInit } from '@angular/core';
import { FormArray, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Params } from '@angular/router';
import { RecipeService } from '../recipe.service';
import { Ingredient } from 'src/app/shared/ingredient.model';

@Component({
  selector: 'app-recipe-edit',
  templateUrl: './recipe-edit.component.html',
  styleUrls: ['./recipe-edit.component.css']
})
export class RecipeEditComponent implements OnInit{
  constructor(private route:ActivatedRoute, private recipeSrv:RecipeService){};

  recipeId!:number;
  isEdit:boolean = false;

  recipeForm!:FormGroup;

  private initForm(){
    let recipeName = "";
    let imageURL = "";
    let description = "";
    let ingredients:Ingredient[] = [];
    if(this.isEdit){
      let recipe = this.recipeSrv.getRecipeById(this.recipeId)
      recipeName = recipe['name'];
      imageURL = recipe['imagePath'];
      description = recipe['description'];
      ingredients = recipe['ingredients'];

    }
    console.log(description);
    this.recipeForm = new FormGroup({
      'name': new FormControl(recipeName,[Validators.required]),
      'imageURL': new FormControl(imageURL,[Validators.required]),
      'description': new FormControl(description,[Validators.required]),
      // 'ingredients': new FormArray(ingredients.map((ing)=>{
      //   return new FormGroup({
      //     name: new FormControl(ing.name),
      //     amount: new FormControl(ing.amount)
      //   })
      // }))
    });
    console.log(this.recipeForm.value);
  }

  getIngredientsControlArray(){
    return (this.recipeForm.get('ingredients') as FormArray).controls;
  }

  addIngredientControl(){
    (this.recipeForm.get("ingredients") as FormArray).push(new FormControl({
      name:null,
      amount:null
    }))
  }

  ngOnInit(): void {
    this.route.params.subscribe((param:Params)=>{
      this.recipeId = param['id'];
      console.log(param);
      this.isEdit = param['id'] != undefined;
      console.log(this.isEdit);
      this.initForm();
    })
  }

  onSubmit(){
    console.log(this.recipeForm);
  }


}
