import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Recipe } from '../../recipe.model';

@Component({
  selector: 'app-recipe-item',
  templateUrl: './recipe-item.component.html',
  styleUrls: ['./recipe-item.component.css']
})
export class RecipeItemComponent {

  @Input()
  recipe:Recipe = new Recipe();

  @Output()
  recipeChangedItem = new EventEmitter<Recipe>();

  onChangeSelectedRecipe(){
    this.recipeChangedItem.emit(this.recipe)
  }
}
<a href="#" class="list-group-item clearfix" (click)="onChangeSelectedRecipe()">
  <div class="col-md-10">
    <h4 class="list-group-item-heading">{{recipe.name}}</h4>
    <p class="list-group-item-text">{{recipe.description}}</p>
  </div>
  <div class="col-md-2">
    <img style="min-height: 50px; min-width: 50px;" src={{recipe.imagePath}} class="img-responsive" alt="">
  </div>
</a>
