import { Component,OnInit } from '@angular/core';
import { FormArray, FormControl, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Params } from '@angular/router';
import { RecipeService } from '../recipe.service';
import { Ingredient } from 'src/app/shared/ingredient.model';

@Component({
  selector: 'app-recipe-edit',
  templateUrl: './recipe-edit.component.html',
  styleUrls: ['./recipe-edit.component.css']
})
export class RecipeEditComponent implements OnInit{
  constructor(private route:ActivatedRoute, private recipeSrv:RecipeService){};

  recipeId!:number;
  isEdit:boolean = false;

  recipeForm!:FormGroup;

  private initForm(){
    let recipeName = "";
    let imageURL = "";
    let description = "";
    let ingredients = new FormArray<FormGroup>([]);
    if(this.isEdit){
      let recipe = this.recipeSrv.getRecipeById(this.recipeId)
      recipeName = recipe['name'];
      imageURL = recipe['imagePath'];
      description = recipe['description'];
      let recipeIngredients:Ingredient[] = recipe['ingredients'];
      recipeIngredients.forEach(ing=>{
        ingredients.push(new FormGroup({
          name:new FormControl(ing.name,[Validators.required]),
          amount: new FormControl(ing.amount,[Validators.required])
        }))
      })
    }

    this.recipeForm = new FormGroup({
      'name': new FormControl(recipeName,[Validators.required]),
      'imageURL': new FormControl(imageURL,[Validators.required]),
      'description': new FormControl(description,[Validators.required]),
      'ingredients': ingredients
    });
  }

  getIngredientsControlArray(){
    return (this.recipeForm.get('ingredients') as FormArray).controls;
  }

  addIngredientControl(){
    (this.recipeForm.get("ingredients") as FormArray).push(new FormGroup({
      name: new FormControl("",[Validators.required]),
      amount:new FormControl("",[Validators.required,Validators.pattern('^[1-9]+[0-')])
    }))
  }

  ngOnInit(): void {
    this.route.params.subscribe((param:Params)=>{
      this.recipeId = param['id'];
      this.isEdit = param['id'] != undefined;
      this.initForm();
    })
  }

  onSubmit(){
    console.log(this.recipeForm);
  }


}
<div class="row">
  <div class="col-xs-12">
    <form [formGroup]="recipeForm" (ngSubmit)="onSubmit()">

      <div class="row">
        <div class="col-xs-12">
          <div class="form-group">
            <label for="name"> Recipe Name </label>
            <input type="text" id="name" formControlName="name" class="form-control">
          </div>
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <div class="form-group">
            <label for="imageURL"> Image URL </label>
            <input type="text" formControlName="imageURL" id="imageURL" class="form-control">
          </div>
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <img src="" alt="" class="img-responsive">
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <div class="form-group">
            <label for="description"> Description </label>
            <textarea type="text" formControlName="description" id="description" class="form-control"></textarea>
          </div>
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12">
          <button role="button" type="button" class="btn btn-default" (click)="addIngredientControl()">Add Ingredient</button>
          <br><br>
        </div>
        <div class="col-xs-12" formArrayName="ingredients">
          <div class="row" *ngFor="let ingredient of getIngredientsControlArray(); let i=index" >
            <div class="form-group" formGroupName={{i}}>
              <div class="col-xs-6">
                <label for={{i}}>Ingredient Name</label>
                <input type="text" formControlName="name" class="form-control">
              </div>
              <div class="col-xs-4">
                <label for="">Ingredient Amount</label>
                <input type="number" formControlName="amount" id="" class="form-control">
              </div>
              <div class="col-xs-2 ">
                <label for=""></label>
                <div class="">
                  <button class="btn btn-danger align-self-center">X</button>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <br>
      <div class="row">
        <div class="col-xs-12">
          <button class="btn btn-success" [ngStyle]="{marginRight:'0.5em'}">{{isEdit ? 'Update' : 'Save'}}</button>
          <button class="btn btn-danger">Delete</button>
        </div>
      </div>
    </form>
  </div>
</div>
