import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = '02_Course_Project';
  testCSD = false;
  onClick(){
    this.testCSD = !this.testCSD;
  }
}
<app-header></app-header>
<div class="app container">
  <div class="row">
    <div class="col-md-12">
      <button class="btn btn-primary">Test CSD</button>
      <ng-template appUnless [unless]="testCSD">
        <h1>Custom Structural Directive</h1>
      </ng-template>
      <app-recipes></app-recipes>
      <app-shopping-lists></app-shopping-lists>
    </div>
  </div>
</div>
