import {Injectable, OnInit} from "@angular/core";
import {HttpClient} from "@angular/common/http"
import { RecipeService } from "../recipes/recipe.service";
import { Recipe } from "../recipes/recipe.model";

@Injectable({
  providedIn:"root"
})
export class DataStorageService implements OnInit{
  constructor(private http:HttpClient,private recipeSrc:RecipeService){}

  saveRecipes(){
    let recipes = this.recipeSrc.getRecipes();
    this.http.put("http://localhost:8000/save",recipes).subscribe(data=>{
      console.log("response",data);
    })
  }

  ngOnInit(): void {
    console.log("Called");
    this.getRecipes().subscribe((recipes)=>{
      console.log("here");
      this.recipeSrc.setRecipes(recipes);
    })
  }
  getRecipes(){
    return this.http.get<any[]>("http://localhost:8000/all");
  }
}
