import {Injectable,EventEmitter} from "@angular/core";
import { Recipe } from "./recipe.model";
import { Ingredient } from "../shared/ingredient.model";

@Injectable({providedIn:'root'})
export class RecipeService{

  private recipes:Recipe[] = [
    new Recipe("Recipe1","lorem ipsum dfdfer santy restgh reongt ljoiuen dreg","https://img.freepik.com/free-photo/fresh-hamburger-with-salad-onion_144627-9522.jpg?size=626&ext=jpg&ga=GA1.2.98251874.1684238197&semt=sph",[new Ingredient("Ingredient1",10)])
    ,new Recipe("Recipe2","lorem ipsum njyjtyt rewdf jtyrt tert ljoiuen dreg","https://img.freepik.com/free-photo/fresh-hamburger-with-salad-onion_144627-9522.jpg?size=626&ext=jpg&ga=GA1.2.98251874.1684238197&semt=sph")
    ,new Recipe("Recipe3","lorem ipsum iuyt mnbhjk restgh reongt ljoiuen dreg","https://img.freepik.com/free-photo/fresh-hamburger-with-salad-onion_144627-9522.jpg?size=626&ext=jpg&ga=GA1.2.98251874.1684238197&semt=sph")
  ];

  public recipeSelectedEvent:EventEmitter<Recipe> = new EventEmitter<Recipe>();

  public getRecipes(){
    return this.recipes.slice(0);
  }
}
