import { Component, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent {
  isLogin = false;

  @ViewChild("authFrom")
  authFrom!:NgForm;

  switchMode(){
    this.isLogin = !this.isLogin;
  }

  onAuth(){
    console.log(this.authFrom);
  }
}
<div class="row">
  <div class="col-xs-12 col-md-6 col-md-offset-3">
    <form #authForm="ngForm" (ngSubmit)="onAuth(au)">
      <div class="form-group">
        <label for="email">Email</label>
        <input ngModel type="text" name="email" id="email" required email class="form-control">
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input ngModel type="text" name="password" id="password" class="form-control">
      </div>
      <div>
        <button  type="submit" class="btn btn-primary">{{isLogin ?"Singin": "Signup"}}</button> &nbsp;
        <button (click)="switchMode()" type="button" class="btn btn-default">{{isLogin ?"Switch To Signup" :'Switch To Login'}}</button>
      </div>
    </form>
  </div>
</div>
