import { Component } from '@angular/core';

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent {
  isLogin = false;

  switchMode(){
    this.isLogin = !this.isLogin;
  }
}
<div class="row">
  <div class="col-xs-12 col-md-6 col-md-offset-3">
    <form >
      <div class="form-group">
        <label for="email">Email</label>
        <input type="text" name="" id="email" class="form-control">
      </div>
      <div class="form-group">
        <label for="password">Password</label>
        <input type="text" name="" id="password" class="form-control">
      </div>
      <div>
        <button  type="button" class="btn btn-primary">{{isLogin ?"Singin": "Signup"}}</button> &nbsp;
        <button (click)="switchMode()" type="button" class="btn btn-default">{{isLogin ?"Switch To Signup" :'Switch To Login'}}</button>
      </div>
    </form>
  </div>
</div>
