import { Component, ElementRef, EventEmitter, Output, ViewChild } from '@angular/core';
import { Ingredient } from 'src/app/shared/ingredient.model';
import { ShoppingService } from '../shopping.service';
import { NgForm, } from "@angular/forms"

@Component({
  selector: 'app-shopping-edit',
  templateUrl: './shopping-edit.component.html',
  styleUrls: ['./shopping-edit.component.css']
})
export class ShoppingEditComponent {
  constructor(private shoppingSrv:ShoppingService){}

  @ViewChild("ingredientForm") ingredientForm!:NgForm;


  onAddIngredient(){
    console.log(this.ingredientForm);
    if(this.ingredientForm.valid){
      let value = this.ingredientForm.value;
      this.shoppingSrv.addNewIngredient(new Ingredient(value.name,value.amount));
    }
  }

}
<div class="row">
  <div class="col-md-12">
    <form (submit)="onAddIngredient()" #ingredientForm="ngForm">
      <div class="row">
        <div class="col-sm-5 form-group">
          <label for="name">Name</label>
          <input type="text" name="name" ngModel required class="form-control" id="name" #ingredientName="ngModel">
          <span class="help-block" *ngIf="!ingredientName.valid && ingredientName.touched">Name Is Required</span>
        </div>
        <div class="col-sm-2 form-group">
          <label for="amount">Amount</label>
          <input type="number" name="amount" ngModel required id="amount" class="form-control" #ingredientAmount="ngModel">
          <span class="help-block" *ngIf="!ingredientAmount.valid && ingredientAmount.touched">Amount Is Required</span>
        </div>
      </div>
      <div class="row">
        <div class="col-sm-12">
          <button [disabled]="ingredientForm.valid" class="btn btn-success" type="submit" >Add</button>
          <button class="btn btn-danger" type="button" [ngStyle]="{marginLeft:'5px'}">Add</button>
          <button class="btn btn-primary" type="button" [ngStyle]="{marginLeft:'5px'}">Clear</button>
        </div>
      </div>
    </form>
  </div>
</div>
