import { Component, OnInit,OnDestroy } from '@angular/core';
import { Recipe } from '../recipe.model';
import { RecipeService } from '../recipe.service';
import { ActivatedRoute, Params, Router } from '@angular/router';
import { Subscription } from 'rxjs';

@Component({
  selector: 'app-recipe-detail',
  templateUrl: './recipe-detail.component.html',
  styleUrls: ['./recipe-detail.component.css']
})
export class RecipeDetailComponent implements OnInit,OnDestroy{

  recipeToDisplay:Recipe = new Recipe();

  recipeSelectedSubscribeId!:Subscription;

  constructor(private recipeService:RecipeService,private route:ActivatedRoute,private router:Router){}

  ngOnInit(): void {
    this.recipeToDisplay = this.recipeService.getRecipeById(this.route.snapshot.params['id']);
    // console.log(this.route.snapshot.params);
    this.route.params.subscribe((params:Params)=>{
      this.recipeToDisplay = this.recipeService.getRecipeById(params['id']);
    })
  }

  ngOnDestroy(): void {
    // this.recipeSelectedSubscribeId.unsubscribe();
  }

  onAddToShoppingList(){
    this.recipeService.addIngredientsToShoppingList(this.recipeToDisplay.ingredients)
  }

  onDeleteRecipe(){
    this.recipeService.deleteRecipe(this.recipeToDisplay.id);
    this.router.navigate(['..'],{relativeTo:this.route});
  }
}
<div class="row">
  <div class="col-md-12">
    <img
    [ngStyle]="{
      maxHeight:'250px',
      width:'100%'
    }"
    src={{recipeToDisplay.imagePath}}
    class="img-responsive" alt="">
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <h1>{{recipeToDisplay.name}}</h1>
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <div class="btn-group" appDropdown>
      <button type="button" class="btn btn-primary dropdown-toggle">Manage Recipe <span class="caret"></span></button>
      <ul class="dropdown-menu">
        <li><a style="cursor: pointer;" (click)="onAddToShoppingList()">Add Ingridents</a></li>
        <li><a [routerLink]="['edit']">Edit Recipe</a></li>
        <li><a style="cursor: pointer;" (click)="onDeleteRecipe()">Delete Recipe</a></li>
      </ul>
    </div>
  </div>
</div>
<div class="row" [ngStyle]="{marginTop:'8px'}">
  <div class="col-md-12">
    <p>{{recipeToDisplay.description}}</p>
  </div>
</div>
<div class="row">
  <div class="col-md-12">
    <ul class="list-group">
      <li class="list-group-item" *ngFor="let ingredient of recipeToDisplay.ingredients">
        {{ingredient.name}} {{ingredient.amount}}
      </li>
    </ul>
  </div>
</div>
